                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 $scene = Scene_Forum_Thread.new($threadid,true,0,@ft_newposts[@sel.index],$scene)
